create table notice(
	notice_id int auto_increment primary key,
    n_title varchar(150),
    n_writer varchar(30),
    n_content text,
    notice_reg_date timestamp default now()
);

create table inquiry(
	inquiry_id int auto_increment primary key,
    in_title varchar(150),
    in_writer varchar(30),
    re_writer varchar(30),
    in_content text,
    inquiry_pw varchar(50),
    public_status int,
    seq int,
    lev int,
    gid int,
    inquiry_reg_date timestamp default now()
);

create table files(
	file_id int auto_increment primary key,
    files varchar(150),
    products_board_id int,
    notice_id int,
    inquiry_id int,
    complete boolean,
    member_id varchar(200)
);

drop table inquiry;
drop table notice;
drop table files;

alter table files add board_distinct int;




update files set complete = true where file_id = 1;

select * from notice;
select max(notice_id) from notice;
select * from files;
select * from inquiry;
select * from inquiry where inquiry_id = 5 and inquiry_pw = 'qwer';
select max(inquiry_id) from inquiry;

delete from notice where notice_id = 6;